def load_analogies(file_path):
    """
    Load Google analogy test set from
    https://aclweb.org/aclwiki/Google_analogy_test_set_(State_of_the_art)
    """
    associations = []
    with open(file_path) as f:
        content = f.readlines()
        for line in content:
            line = line.rstrip("\n")
            line = line.lower()
            elem_0, elem_1, elem_2, elem_3 = line.split(" ")
            association = {"positive": [elem_0, elem_3], "negative": [elem_1], "expected_output":elem_2}
            associations.append(association)
    return associations

def build_tag_to_idx(distinct_tags, change_output={}, default='other'):
    """
    Build mapping for tags, with possibility to map tags to new values.
    """
    tag_to_idx = {}
    distinct_tags.remove(default) # to be sure default tag is set to 0
    #
    output_tags = set()
    for tag in distinct_tags:
        if tag in change_output:
            output_tag = change_output[tag]
        else:
            output_tag = tag
        tag_to_idx[tag] = {"tag": output_tag}
        output_tags.add(output_tag)
    tag_to_idx[default] = {"tag": default}
    #
    if 'other' in output_tags:
        output_tags.remove('other')
    output_tags = ['other'] + list(output_tags)
    #
    for idx, tag in enumerate(tag_to_idx):
        tag_to_idx[tag]['idx'] = output_tags.index(tag_to_idx[tag]['tag'])
    #
    return tag_to_idx

def ner_evaluation(model, X, Y_ref, tag_to_idx):
    """
    Evaluation of NER model on X.
    """
    Y_probs = model.predict(X)
    Y_pred = np.argmax(Y_probs, axis=-1)
    #
    idx_to_tag = {tag_to_idx[tag]['idx']:tag_to_idx[tag]['tag'] for tag in tag_to_idx}
    #
    all_true_labels = []
    all_predictions = []
    for d, document in enumerate(Y_ref):
        for t, token in enumerate(Y_ref[d]):
            all_true_labels.append(idx_to_tag[Y_ref[d][t]])
            all_predictions.append(idx_to_tag[Y_pred[d][t]])
    #
    report = classification_report(all_true_labels, all_predictions, output_dict=True)
    return report
